mapper.select:
select count(*) from tbl_card 
where name=#{name} and cardNum=#{cardNum} 
and pwd = #{pwd} and bankName = #{bankName} and validateMonth =#{validateMonth} and validateYear=#{validateYear}

mapper.update:
update tbl_card 
set balance = balance + #{money}
where name=#{cardVO.name} and cardNum=#{cardVO.cardNum} 
and pwd = #{cardVO.pwd} and bankName = #{cardVO.bankName} and validateMonth =#{cardVO.validateMonth} and validateYear=#{cardVO.validateYear}

update tbl_card 
set balance = balance - #{money}
where name=#{cardVO.name} and cardNum=#{cardVO.cardNum} 
and pwd = #{cardVO.pwd} and bankName = #{cardVO.bankName} and validateMonth =#{cardVO.validateMonth} and validateYear=#{cardVO.validateYear}
